INSERT INTO "NEWS" ("ANNOUNCEMENT_ID", "PUBLISH_TIME", "PUBLISH_POS", "TITLE", "CONTENTS", "LIKES", "VIEW_COUNT", "administratorAdminId") VALUES ('152155112448', TO_TIMESTAMP('2023-06-15 09:28:59.4772964', 'SYYYY-MM-DD HH24:MI:SS:FF7'), '1', '公司新能源汽车展览会通知', '我们高兴地宣布，公司将参加今年的新能源汽车展览会，这是一个展示我们最新创新和技术的绝佳机会。我们的展位位于展览会主厅，将展示我们最新的电动汽车和可再生能源技术。我们邀请所有员工前来参观我们的展台，以便更好地了解我们的产品和技术。此外，如果您有兴趣成为我们展会团队的志愿者，请联系HR部门报名。', '0', '0', '3');
