INSERT INTO "NEWS" ("ANNOUNCEMENT_ID", "PUBLISH_TIME", "PUBLISH_POS", "TITLE", "CONTENTS", "LIKES", "VIEW_COUNT", "administratorAdminId") VALUES ('167125550080', TO_TIMESTAMP('2023-08-12 20:16:17.2970059', 'SYYYY-MM-DD HH24:MI:SS:FF7'), '2', '安全提醒：智能驾驶辅助系统', '我们重要提醒车主，在使用智能驾驶辅助系统时，仍需保持警惕，全程注意道路情况。这些系统可以提高驾驶的安全性和舒适性，但不应替代您的驾驶责任。请遵守交通规则，始终保持对道路的关注，以确保您的安全和他人的安全。', '0', '0', '2');
